mapper.select:
SELECT CARD_ID, CARD_NM, CARD_SORT_NUM, EXPOSURE_YN, FIX_YN, SERVICE_CATEGORY FROM TB_CARD WHERE 1=1 AND EXPOSURE_YN ='Y'
AND SERVICE_CATEGORY = #{serviceCategory}
ORDER BY CARD_SORT_NUM ASC

SELECT CARD_ID, CARD_NM, CARD_SORT_NUM, EXPOSURE_YN, FIX_YN, SERVICE_CATEGORY, REG_ID, TO_CHAR(REG_DT, 'YYYY-MM-DD') AS REG_DT, UPD_ID, TO_CHAR(UPD_DT, 'YYYY-MM-DD') AS UPD_DT FROM TB_CARD WHERE CARD_ID IN
#{item}
AND SERVICE_CATEGORY = #{serviceCategory}
ORDER BY REG_DT DESC,CARD_SORT_NUM ASC

mapper.update:
UPDATE TB_CARD
	SET 
		 EXPOSURE_YN = #{exposureYn}
		,FIX_YN= #{fixYn}
		,CARD_NM = #{cardNm}
		,CARD_SORT_NUM  = #{cardSortNum}
		,SERVICE_CATEGORY =#{serviceCategory}
		,CARD_APP_ID = #{cardAppId}
		,UPD_ID = #{updId}
		,UPD_DT = SYSDATE
 WHERE CARD_ID = #{cardId}

mapper.select:
SELECT 
		COUNT(CARD_NM)
FROM TB_CARD 
WHERE CARD_NM = #{cardNm} 

	AND CARD_ID != #{cardId} 


	AND SERVICE_CATEGORY = #{serviceCategory}

SELECT 
		COUNT(CARD_SORT_NUM)
FROM TB_CARD 
WHERE CARD_SORT_NUM = #{cardSortNum}

	AND CARD_ID != #{cardId} 


	AND SERVICE_CATEGORY = #{serviceCategory}

mapper.update:
UPDATE TB_EVENT_CARD
	SET 
		 SERVICE_CATEGORY  = #{serviceCategory}
		,EVENT_CARD_URL    = #{eventCardUrl}
		,EXPOSURE_START_DT = TO_DATE(#{exposureStartDt},'yyyymmddhh24miss')
		,EXPOSURE_END_DT   = TO_DATE(#{exposureEndDt},'yyyymmddhh24miss')
		,UPD_ID = #{updId}
		,UPD_DT = SYSDATE
		,CARD_ID = #{cardId}
		,EVENT_CARD_TYPE = #{eventCardType}
 WHERE EVENT_CARD_ID = #{eventCardId}

UPDATE 
   TB_CARD
 SET EXPOSURE_YN ='Y'
 ,CARD_SORT_NUM = #{cardSortNum}
 ,UPD_DT = SYSDATE
 ,UPD_ID = #{updId}
 WHERE CARD_ID=#{cardId}
