mapper.select:
SELECT * FROM ( SELECT (@ROWNUM := @ROWNUM+1) RNUM ,SEQ_NOTICE_ID AS NOTICE_ID ,TITLE ,CONTENT ,LINK_URL ,USE_YN ,INSERT_ID ,DATE_FORMAT(INSERT_DATE, '%Y-%m-%d %H:%i') AS INSERT_DATE ,UPDATE_ID ,IFNULL(DATE_FORMAT(UPDATE_DATE, '%Y-%m-%d %H:%i'), '-') AS UPDATE_DATE FROM TB_NOTICE ,(SELECT @ROWNUM := 0 FROM DUAL) TMP
AND USE_YN = #{useYn}
AND TITLE LIKE '%${content}%'
AND CONTENT LIKE '%${content}%'
AND DATE_FORMAT(INSERT_DATE, '%Y%m%d') >= #{startDate}
AND DATE_FORMAT(INSERT_DATE, '%Y%m%d') <= #{endDate}
AND DATE_FORMAT(UPDATE_DATE, '%Y%m%d') >= #{startDate}
AND DATE_FORMAT(UPDATE_DATE, '%Y%m%d') <= #{endDate}
ORDER BY USE_YN DESC , NOTICE_ID DESC ) AS A WHERE A.RNUM BETWEEN #{startCount} AND #{endCount}

SELECT COUNT(SEQ_NOTICE_ID)
  FROM TB_NOTICE


			AND USE_YN = #{useYn}


			AND TITLE LIKE '%${content}%'


			AND CONTENT LIKE '%${content}%'


		    AND DATE_FORMAT(INSERT_DATE, '%Y%m%d')  >= #{startDate}


			AND DATE_FORMAT(INSERT_DATE, '%Y%m%d') <=  #{endDate}


		    AND DATE_FORMAT(UPDATE_DATE, '%Y%m%d')  >= #{startDate}


			AND DATE_FORMAT(UPDATE_DATE, '%Y%m%d') <=  #{endDate}

mapper.update:
UPDATE TB_NOTICE
   SET TITLE = #{title, jdbcType=VARCHAR}
   	   , CONTENT = #{content, jdbcType=VARCHAR}
   	   , LINK_URL = #{linkUrl, jdbcType=VARCHAR}
   	   , USE_YN = #{useYn, jdbcType=VARCHAR}
   	   , UPDATE_ID = #{updateId, jdbcType=VARCHAR}
   	   , UPDATE_DATE = NOW()
 WHERE SEQ_NOTICE_ID = #{noticeId}

UPDATE	TB_BOARD
   SET	OPT1 = #{noticeShowYn}
 WHERE	SEQ_BOARD_MST_ID = 'notice_on_off'
