mapper.insert:
INSERT INTO TB_MENU (
		MENU_ID
	  , MENU_NM
	  , PARENTMENU_ID
	  , MENU_DEPTH
	  , ORDER_NO
	  , REG_ID
	  , REG_DT
	  , UPD_ID
	  , UPD_DT
	  , USE_YN
) VALUES (
		#{menuId}
	  , #{menuNm}
	  , #{parentmenuId}		
	  , #{menuDepth}		
	  , #{orderNo}
	  , #{regId}
	  , SYSDATE
	  , #{updId}
	  , SYSDATE
	  , #{useYn}
)

mapper.update:
UPDATE TB_MENU
   SET MENU_NM 			= #{menuNm}
     , PARENTMENU_ID 	= #{parentmenuId}
     , UPD_ID			= #{updId}
     , UPD_DT			= SYSDATE
     , ORDER_NO			= #{orderNo}
     , MENU_DEPTH		= #{menuDepth}
     , USE_YN			= #{useYn}
 WHERE MENU_ID			= #{menuId}

UPDATE TB_MENU SET ORDER_NO = #{orderNo} /* 정렬 번호 */
, PARENTMENU_ID = #{parentmenuId} /* 부모메뉴 아이디 */
, UPD_ID = #{updId} /* 수정 아이디 */ , UPD_DT = SYSDATE /* 수정 일시 */ WHERE MENU_ID = #{menuId}
